declare module '*.svg' {
  import React = require('react')
  export const ReactComponent: React.SFC<React.SVGProps<SVGSVGElement>>
  const src: number
  export default src
}
declare module '*.jpg' {
  const content: number
  export default content
}

declare module '*.png' {
  const content: number
  export default content
}

declare module '*.json' {
  const content: number
  export default content
}
